def versions_dict(pkgs=("numpy","pandas","matplotlib","scipy","sklearn")) -&gt; Dict[str, Optional[str]]:
    out = {}
    for p in pkgs:
        mod = try_import(p if p!="sklearn" else "sklearn")
        out[p] = getattr(mod, "__version__", None) if mod else None
    return out

